import React, {Component} from 'react'
import './task-toggle.css'



export default class TaskToggle extends Component {

    state = {
        value: ''
    }
   //  debounce = (func, time)=>{
   //      let debounceActive;
   //     return  function (){
   //          clearTimeout(debounceActive)
   //          debounceActive = setTimeout( funct.bind(this, arguments), time)
   //      }
   //  }
   // const deboun = debounce(mainFunction, 500);

    editTask =(event)=>{
        const {id, mainFunction} = this.props;
        const val = event.target.value
        return  setTimeout(mainFunction.apply(this, (id, '', val)), 300)
        // if( event.target.key === 13){
        //     console.log(id)

            // return  mainFunction(id, '')

    }

    changeStatus = ()=>{
        const {id,mainFunction} = this.props;

        console.log(id)
        // let sassed = false;
        let newStatus = '';
        if(this.props.mainClass === 'completed'){
            newStatus = ''
            // sassed = false;
        }
        else {
            newStatus = 'completed'
            // sassed = true;
        }
        // this.setState(({completed})=> {
        //     return  {
        //         completed: sassed
        //     }
        // })
        return  mainFunction(id, newStatus)
    }

    render() {
        const {className, type, value} = this.props;
        if(value){
            return  <input onChange={(event)=>{this.editTask(event)}}
                           defaultValue={value} type={type} className={className}/>
        }
        return (
            <input onClick={this.changeStatus}  type={type} className={className}/>
        );
    }

}



