import React, {Component} from 'react'
import TaskListItem from "../task-list-item/task-list-item";
import './task-list.css'



export default class TaskList extends Component {


    state = {
        listToDo: [
            { text: 'Completed', className: 'completed', id: 1},
            { text: 'Editing', className: 'editing', id:  2},
            { text: 'Active', className: '', id:  3},
        ]
}

    addElement =()=>{
        this.setState(({listToDo})=>{
            const newArr =  JSON.parse(JSON.stringify(listToDo))
            const newId = newArr[newArr.length - 1].id + 1;
            const newElement = {
                text: "random", className: '', id: newId
            }
            newArr.unshift(newElement)
            return  {
                listToDo: newArr
            }
        })
    }


    taskEditing = (id, task, val)=>{
        this.setState(({listToDo})=>{
            const newArr =  JSON.parse(JSON.stringify(listToDo))
            const inx = listToDo.findIndex((el)=>el.id === id)
            if(task === 'del'){
                newArr.splice(inx, 1)
            }
            else if(val) {
                newArr[inx].className = 'completed'
                newArr[inx].text = val;
            }else{
                newArr[inx].className = `${task}`
            }
            return {
                listToDo: newArr
            }
        })
    }

    render() {
        const taskArr =  this.state.listToDo;
        const taskList = taskArr.map(({className, id, text})=>{
            return <TaskListItem mainFunction={this.taskEditing}
                                 className={className} id={id} key={id}
                                 other={text}/>
        })

        return (
            <ul className='todo-list'>
                {taskList}
                <button onClick={this.addElement}  className='newSa' />
            </ul>
        );
    };
};
