import React, {Component} from 'react';
import PropTypes from 'prop-types'
import {formatDistanceToNow} from 'date-fns';

export default class TaskLabel extends Component {

    static defaultProps = {
        text: 'New Task',
        timeOfCreate: new Date()
    }

    static propTypes = {
        timeOfCreate: PropTypes.instanceOf(Date).isRequired,
        // timeOfCreate: PropTypes.number.isRequired, // изменить на Date когда решу проблему.
        text: PropTypes.string
    }

    state = {
        time: formatDistanceToNow(this.props.timeOfCreate, {includeSeconds: true}),
    }


    tick = () => {
        this.setState({
            time: formatDistanceToNow(this.props.timeOfCreate, {includeSeconds: true}),
        });
    }

    componentDidMount = () => {
        this.intervalID = setInterval(() => this.tick(), 1000);
    }

    componentWillUnmount = () => {
        clearInterval(this.intervalID);
    }

    render() {
        return (
            <label>
                <span className='description'>{this.props.text}</span>
                <span className='created'>  created {`${this.state.time}`} ago</span>
            </label>
        )
    }
}
