import React, {Component} from 'react';
import PropTypes from 'prop-types';
import FilterButton from "../filter-button/filter-button";
import './task-filter.css';

export default class TaskFilter extends Component {

    static defaultProps = {
        onClickTaskFilterBtnHandler: () => {}
    }

    static propTypes = {
        onClickTaskFilterBtnHandler: PropTypes.func.isRequired,
    }

    key = 1;
    state = {
        filtersList: [
            {label: 'All', className: 'selected', key: 10, mainClass: 'all'},
            {label: 'Active', className: '', key: 20, mainClass: ''},
            {label: 'Completed', className: '', key: 30, mainClass: 'completed'}
        ]
    }


    onClickTaskFilterBtnHandler = (label) => {
        const {selectTaskFilter} = this.props;
        this.setState(({filter}) => {
            const newFilterList = filter.map((el) => {
                if (el.label === label) {
                    el.className = 'selected';
                    selectTaskFilter(el.mainClass);
                } else {
                    el.className = '';
                }
                return el;
            });
            return {
                filter: newFilterList
            }
        });

    };


    render() {
        const {filtersList} = this.state;
        const filterItem = filtersList.map(({...item}) => {
            return <li key={this.key++}>
                      <FilterButton onClickTaskFilterBtnHandler={this.onClickTaskFilterBtnHandler}
                              {...item}/>
                  </li>
        });
        return (
            <ul className='filters'>
                {filterItem}
            </ul>
        );
    };
};
