import React, {Component} from 'react'
import './task-toggle.css'



export default class TaskToggle extends Component {

    // state = {
    //     competed: false
    // }
    editTask =(event)=>{
        if(event.keyCode === 13){
            const {id,mainFunction} = this.props;
            return  mainFunction(id, '')
        }
    }

    changeStatus = ()=>{
        const {id,mainFunction} = this.props;
        // let sassed = false;
        let newStatus = '';
        if(this.props.mainClass === 'completed'){
            newStatus = ''
            // sassed = false;
        }
        else {
            newStatus = 'completed'
            // sassed = true;
        }
        // this.setState(({completed})=> {
        //     return  {
        //         completed: sassed
        //     }
        // })
        return  mainFunction(id, newStatus)
    }

    render() {
        const {className, type, value} = this.props;
        if(value){
            return  <input onKeyDown={(event)=>{this.editTask(event)}} defaultValue={value} type={type} className={className}/>
        }
        return (
            <input onClick={this.changeStatus}  type={type} className={className}/>
        );
    }

}



