import React, {Component} from  'react'

export default class TaskStatus extends Component {

    state = {
        done: false
    }

    onClickSpanHandler = () =>{
        console.log(this.props.className, this.state.done)
       if(this.state) this.setState({done: true})
        else  this.setState({done: false})
    }

    render() {
       const {done} = this.state;



    let {className, test} = this.props


        if(className ==='description'){
            let text = '';
            if(test === 'completed'){
                text += 'Completed task';
            }
            else  if(test === 'editing'){
                text += 'Editing task';
            }
             else text += 'Active task';
            return <span onClick={this.onClickSpanHandler} className={className} >{text}</span>

        }

        else if(className === 'created') {
            return  <span className={className}>  created ____ ago</span>

        }

    }

}

