import React, {Component} from 'react'
import './task-toggle.css'



export default class TaskToggle extends Component {

    state = {
        value: ''
    }


    editTask =(event)=>{
        const {id, mainFunction} = this.props;
        const val = event.target.value
        return  mainFunction(id, '', val)
        // if( event.target.key === 13){
        //     console.log(id)

            // return  mainFunction(id, '')

    }


    render() {
        const {className, type, value,changeStatus,id,mainFunction} = this.props;
        let inputField = <input onChange={(event)=>{this.editTask(event)}}
                       defaultValue={value} type={type} className={className}/>;
        let checkbox =  <input onClick={()=>changeStatus(id)}  type={type} className={className}/>;

        return (value)? inputField : checkbox;

        // if(value){
