import React, {Component} from 'react'
import TaskListItem from "../task-list-item/task-list-item";
import './task-list.css'
import TaskToggle from "../task-toogle/task-toggle";


export default class TaskList extends Component {


    state = {
        listToDo: [
            { className: 'completed', id: 1},
            { className: 'editing', id:  2},
            { className: '', id:  3},
        ]
}



  delElements=(id)=>{
     this.setState(({listToDo})=>{
         const inx = listToDo.findIndex((el)=>el.id === id)
         const before = listToDo.slice(1, inx)
         const after = listToDo.slice(inx + 1)
         const newArr = [...before, ...after]
         return { listToDo: newArr }
     })
  }
    completedTask=(id)=>{
        this.setState(({listToDo})=>{
            const newArr = {...listToDo}
            const inx = listToDo.findIndex((el)=>el.id === id)
            console.log( newArr[inx].className = 'editing')
            // const before = listToDo.slice(1, inx)
            // const after = listToDo.slice(inx + 1)
            // const newArr = [...before, ...after]
            console.log(newArr)
            return { listToDo: newArr}
        })
    }


    render() {
      const props = this.props

        const testElements = this.state.listToDo.map(({className, id})=>{
            return <TaskListItem testFun={this.completedTask} className={className} id={id}/>
        })


        return (
        <ul   className='todo-list'>

            {testElements}
            {/*<TaskListItem testFun={this.delCo} className="completed" />*/}
            {/*<TaskListItem className="editing"/>*/}
            {/*<TaskListItem />*/}
        </ul>

    );
    };
};
