import React, {Component} from 'react'
import './task-toggle.css'



export default class TaskToggle extends Component {

    state = {
        competed: false
    }


    onCompleted = ()=>{
        const {id,mainFunction} = this.props;
            if(this.props.mainClass === 'completed'){
                this.setState(({completed})=> {
                    return  {
                        completed: true
                    }
                })
                return  mainFunction(id, '')
            } else {
                this.setState(({completed})=> {
                    return  {
                        completed: false
                    }
                })
                return  mainFunction(id, 'completed')
            }
        }




    render() {
        const {className, type, value} = this.props;
        if(value){
            return  <input defaultValue={value} type={type} className={className}/>
        }
        return (
            <input onClick={this.onCompleted}  type={type} className={className}/>
        );
    }

}
