import React, {Component} from  'react';
import FilterButton from "../filter-button/filter-button";
import './task-filter.css';

export default class TaskFilter extends Component {

    state ={
        filter:
            [
                {label: 'All', className: 'selected'},
                {label: 'Active', className: ''},
                {label: 'Selected', className: ''},
            ]

    }


    changeFilter =(label)=>{
        this.setState(({filter})=>{
           const newFilter =  JSON.parse(JSON.stringify(this.state.filter));
           
        })

    }


    render() {
        const filter = this.state.filter
        const filterItem = filter.map(({label,className})=>{
            return <li><FilterButton changeFilter={this.changeFilter}
                                    className={className}
                                    label={label}/></li>
        })

        return (
            <ul className='filters'>
                {filterItem}
            </ul>
        );
    }

}



