import React, {Component} from 'react'
import TaskListItem from "../task-list-item/task-list-item";
import './task-list.css'



export default class TaskList extends Component {


    state = {
        listToDo: [
            { value: 'Дописать', className: 'completed', id: 1},
            { value: 'Дописать Список Дел', className: 'editing', id:  2},
            { value: 'Дописать Список Дел', className: '', id:  3},
        ]


}

    taskEditing = (id, task)=>{
        this.setState(({listToDo})=>{
            const newArr =  JSON.parse(JSON.stringify(listToDo))
            const inx = listToDo.findIndex((el)=>el.id === id)
            if(task === 'del'){
                newArr.splice(inx, 1)
            }
            else  {
                newArr[inx].className = `${task}`
            }
            return {
                listToDo: newArr
            }
        })
    }

    render() {
        const taskArr =  this.state.listToDo;
        const taskList = taskArr.map(({className, id, value})=>{
            return <TaskListItem mainFunction={this.taskEditing}
                                 className={className}  key={id}
                                 other={value}/>
        })

        return (
            <ul className='todo-list'>
                {taskList}
            </ul>
        );
    };
};
