import React, {Component} from 'react';
import './new-task-form.css';

export default class NewTaskForm extends Component {

    static defaultProps = {
        className: 'new-todo',
        holder: "Input do wish",
        type: 'text',
    }

    state = {
        value: ''
    }

    onSubmitInputTask(e){
        const {addElement} = this.props
        let newTask = this.state.value = ''? "Новая задача" : this.state.value;
        // if(newTask === '') newTask = "Новая задача";
        e.preventDefault();
        addElement(newTask)
        this.setState(()=>{
            return{
                value: ''
            }
        })
    }

    onChangeInputHandler = (e) =>{
            this.setState(({value})=>{
                return({
                        value: e.target.value
                })
            })
    }

    render() {
    const {holder, className, type} = this.props
        return <form onSubmit={(e)=>this.onSubmitInputTask(e)}>
                <input onChange={(e)=>this.onChangeInputHandler(e)}
                       className={className} type={type} placeholder={holder}
                       value={this.state.value}/>
        </form>
    }


}
