import React , {Component}from 'react';
import './new-task-form.css'

export default class NewTaskForm extends Component {
    state = {
        value: ''
    }
    inputValue = (e) =>{
        if (e.keyCode == 13) {
            this.setState(({value})=>{
                return(
                    {value: e.target.value}
                )
            })
        }
    }

    render() {
    const {holder, className} = this.props

        return <form>
            <input onChange={(e)=>this.inputValue(e)} className={className} type='text' placeholder={holder} defaultValue={this.state.value}/>
        </form>
    }


}
