import React, {Component} from 'react'
import './task-toggle.css'
import TaskList from "../task-list/task-list";


export default class TaskToggle extends Component {

    state = {
        competed: false
    }

    onCompleted = ()=>{
        // this.state.competed?  this.setState({ competed: false}) :  this.setState({ competed: true});
      this.setState(({competed})=> {
           console.log(competed)
          return {competed: !competed}

      })
    }

    render() {
        const {className, value, type, testFun} = this.props
        if(this.state.competed) {
           return testFun
        }
        return (
            <input onClick={this.onCompleted} type={type} className={className} value={value}/>
        );
    }

}
