import React, {Component} from 'react'
import './task-toggle.css'


export default class TaskToggle extends Component {

    state = {
        competed: false
    }
    onCompleted = ()=>{

        if(this.state.competed){

          this.setState({
                competed: false
            })
        } else if(!this.state.competed){
         this.setState({
                competed: true
            })
        }
        console.log(this.state)
    }

    render() {
        const {className, value, type} = this.props
        if(this.state.competed) {
            console.log('РАБОТАА')
        }
        return (
            <input onClick={this.onCompleted} type={type} className={className} value={value}/>
        );
    }

}
