import React, {Component} from 'react';
import './new-task-form.css';

export default class NewTaskForm extends Component {

    static defaultProps = {
        className: 'new-todo',
        holder: "Input do wish",
        type: 'text',
    }

    state = {
        value: ''
    }
    changeInputValue = (value)=>{
        this.setState(()=>{
            return{
                value: value
            }
        })
    }
    onSubmitInputHandler(e){
        const {addElement} = this.props
        if(this.state.value === '') this.state.value = "Новая задача";
        e.preventDefault();
        addElement(this.state.value)
        this.changeInputValue('')

    }

    onChangeInputHandler = (e) =>{
        this.changeInputValue( e.target.value)
    }

    render() {
    const {holder, className, type} = this.props
        return <form onSubmit={(e)=>this.onSubmitInputHandler(e)}>
                <input onChange={(e)=>this.onChangeInputHandler(e)}
                       className={className} type={type} placeholder={holder}
                       value={this.state.value}/>
        </form>
    }


}
