import React, {Component} from 'react'
import './task-toggle.css'
import TaskList from "../task-list/task-list";


export default class TaskToggle extends Component {

    state = {
        competed: false
    }

    onCompleted = ()=>{
        const {id,testFun, value} = this.props;

            if(this.props.mainClass === 'completed'){
                this.setState(({completed})=> {
                    return  {
                        completed: true
                    }
                })
                return  testFun(id, '')
            } else {
                this.setState(({completed})=> {
                    return  {
                        completed: false
                    }
                })
                return  testFun(id, 'completed')
            }

        }




    render() {
        const {className, type, value} = this.props
        return (
            <input onClick={this.onCompleted}  value={value} type={type} className={className}/>
        );
    }

}
