import React, {Component} from 'react';
import PropTypes from 'prop-types'
import './new-task-form.css';

export default class NewTaskForm extends Component {

    static defaultProps = {
        className: 'new-todo',
        type: 'text',
        holder: "Input do wish",
        onChangeInputHandler: () => {},
        onSubmitFormHandler: () => {},
    }

    static propTypes = {
        onChangeInputHandler: PropTypes.func.isRequired,
        onSubmitFormHandler: PropTypes.func.isRequired,
        className: PropTypes.string.isRequired,
        type:  PropTypes.string.isRequired,
        holder:  PropTypes.string.isRequired,
    }

    state = {
        value: ''
    }

    changeInputValue = (value) => {
        this.setState({value: value});
    }

    onSubmitFormHandler(e) {
        e.preventDefault();
        this.props.addTaskToListToDo(this.state.value);
        this.changeInputValue('');
    }

    onChangeInputHandler = (e) => {
        this.changeInputValue(e.target.value);
    }

    render() {
        const {holder, className, type} = this.props;
        return <form onSubmit={(e) => {
            this.onSubmitFormHandler(e)
        }}>
            <input onChange={this.onChangeInputHandler}
                   className={className} type={type} placeholder={holder}
                   value={this.state.value}/>
        </form>
    }
}
