import React, {Component} from  'react'

export default class TaskStatus extends Component {

    state = {
        done: false
    }

    onClickSpanHandler = () =>{
       if(this.state) this.setState({done: true})
        else  this.setState({done: false})
    }

    render() {
       const {done} = this.state;



    let {className, test} = this.props


        if(className ==='description'){
            if(test === 'completed'){
                if(done){
                    return <span onClick={this.onClickSpanHandler}  className='editing'>Completed task</span>
                }

                else  return <span onClick={this.onClickSpanHandler} className={className}>Completed task</span>
            }
            else  if(test === 'editing'){
                return <span  onClick={this.onClickSpanHandler} className={className}>Editing task</span>
            }
            // else if (!done) return <span  onClick={this.onClickSpanHandler} className={className}>Active task</span>
            else if (done) return <span  onClick={this.onClickSpanHandler} className='completed'>Active task</span>
        }

        else if(className === 'created') {
            return  <span className={className}>  created ____ ago</span>

        }

    }

}

