import React, {Component} from 'react'
import './task-toggle.css'
import TaskList from "../task-list/task-list";


export default class TaskToggle extends Component {

    state = {
        competed: false
    }

    onCompleted = ()=>{
        if(this.props.label){
            this.setState(({completed})=> {
               return  {
                    completed: false
                }
            })
        }
      this.setState(({competed})=> {

          return {competed: !competed}
      })

       return  this.props.testFun(this.props.id, 'completed')
    }

    render() {
        const {className, value, type, id,testFun} = this.props

        return (
            <input onClick={this.onCompleted}  type={type} className={className} value={value}/>
        );
    }

}
