import React, {Component} from 'react'
import './task-toggle.css'



export default class TaskToggle extends Component {

    state = {
        value: ''
    }


    editTask =(event)=>{
        console.log(event.target.value)
        const {id, mainFunction, value} = this.props;
        const val = event.target.value
        if(event.keyCode == 13){
          value = event.target.value
        }
    }


    render() {
        const {className, type, value,changeStatus,id} = this.props;
        console.log(value)
        let inputField = <input onKeyDown={(event)=>{(this.editTask(event))}}
                                defaultValue={value} type={type} className={className}/>;
        let checkbox =  <input onClick={()=>changeStatus(id)}  type={type} className={className}/>;
        return (value)? inputField : checkbox;
    }

}

