import React, {Component} from 'react';
import Header from "../header/header";
import Main from "../main/main";
import './app.css'

export default class App extends Component{

    maxID = 10;
    state = {
        listToDo: [
         // this.createNewTask('ghg'),
         //    this.createNewTask('lkk')
         {
            label: 'label',
            className: '',
            id:  this.maxID++
        }
        ]
    }

    createNewTask = (label)=>{
        return {
           label: label,
            className: '',
            id:  this.maxID++
        }
    }


    addElement =()=>{
        this.setState(({listToDo})=>{
            const newArr =  JSON.parse(JSON.stringify(listToDo))
            const newElement = this.createNewTask('random')
            newArr.unshift(newElement)
            console.log(newArr)
            return  {
                listToDo: newArr
            }
        })
    }


    taskEditing = (id, task, val)=>{
        this.setState(({listToDo})=>{
            const newArr =  JSON.parse(JSON.stringify(listToDo))
            const inx = listToDo.findIndex((el)=>el.id === id)
            if(task === 'del'){
                newArr.splice(inx, 1)
            }
            else if(val) {
                newArr[inx].className = 'completed'
                newArr[inx].text = val;
            }else{
                newArr[inx].className = `${task}`
            }
            return {
                listToDo: newArr
            }
        })
    }


    render() {
    return (
        <section className='todoapp'>
            <Header/>
            <Main addElement={this.addElement} taskEditing={this.taskEditing} createNewTask={this.createNewTask} arr={this.state}/>
        </section>
    );
}

}

